--
-- 表的结构 `recruit`
--

CREATE TABLE IF NOT EXISTS `recruit` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `position` varchar(100) NOT NULL,
  `salary` varchar(20) NOT NULL,
  `quantity` int(3) NOT NULL,
  `place` varchar(50) NOT NULL,
  `date` varchar(30) NOT NULL,
  `description` text NOT NULL,
  `qualification` text NOT NULL,
  `treatment` text NOT NULL,
  `contact` varchar(200) NOT NULL,
  PRIMARY KEY (`id`)
) ENGINE=MyISAM  DEFAULT CHARSET=utf8 AUTO_INCREMENT=19 ;

--
-- 转存表中的数据 `recruit`
--

INSERT INTO `recruit` (`id`, `position`, `salary`, `quantity`, `place`, `date`, `description`, `qualification`, `treatment`, `contact`) VALUES
(15, '招商专员包食宿', '3000-5000', 10, '广州白云', '2016-08-19  16:17', ' <p>\r\n	<br />\r\n</p>\r\n<p>\r\n	1、平时销售人员应做好记录，做好客户分析，及时传递信息；\r\n</p>\r\n<p>\r\n	2、负责呼入电话的接听，了解客户信息并录入系统；\r\n</p>\r\n<p>\r\n	3、对公司分配的客户进行回访跟踪并及时邀约；\r\n</p>\r\n<p>\r\n	4、管理维护客户关系以及客户间的长期战略合作计划。\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<br />\r\n</p> ', '<p>\r\n	1、年龄23-35岁，口齿清晰，普通话流利，\r\n</p>\r\n<p>\r\n	2、对销售工作有较高的热情；\r\n</p>\r\n<p>\r\n	3、具备优秀的学习能力，掌握优秀的沟通技巧；\r\n</p>\r\n<p>\r\n	4、性格坚韧，思维敏捷，具备良好的应变能力和抗压能力；\r\n</p>\r\n<p>\r\n	5、有强烈的事业心、责任心和积极的工作态度，优秀的团队管理精神；\r\n</p>\r\n<p>\r\n	6、大专以上学历，男女不限，有软件、互联网及餐饮行业、招商加盟、快销行业、保险行业等工作经验者优先。\r\n</p>', '<p>\r\n	1、无责任底薪2500-3500元起，业绩优秀者月入逾三万\r\n</p>\r\n<p>\r\n	2、底薪+高提成+高奖金+学习培训\r\n</p>', '<p>\r\n	王先生 15112292900\r\n</p>\r\n<p>\r\n	<br />\r\n</p>'),
(16, '行政后勤', '面议', 2, '广州白云', '2016-08-19  16:22', ' <p>\r\n	1、及时、准确接听/转接电话，如需要，记录留言并及时转达；\r\n</p>\r\n<p>\r\n	2、接待来访客人并及时准确通知被访人员；\r\n</p>\r\n<p>\r\n	3、负责办公区域的环境维护，保证设备安全及正常运转（包括复印机、空调及打卡机等）；\r\n</p>\r\n<p>\r\n	4、完成上级主管交办的其它工作\r\n</p>\r\n<p>\r\n	<br />\r\n</p> ', '<p>\r\n	1、女，形象好，气质佳，年龄18—24岁，身高1.62以上；\r\n</p>\r\n<p>\r\n	2、较强的服务意识，熟练使用电脑办公软件；\r\n</p>\r\n<p>\r\n	3、具备良好的协调能力、沟通能力，负有责任心，性格活泼开朗，具有亲和力；\r\n</p>\r\n<p>\r\n	4、具备一定商务礼仪知识。\r\n</p>\r\n<p>\r\n	5、中专及以上学历，1年相关工作经验，应届毕业生条件优秀者可考虑；\r\n</p>\r\n<p>\r\n	<br />\r\n</p>', '<p>\r\n	1、底薪+高提成+高奖金+住房补贴+包吃+学习培训+年终旅游\r\n</p>', '<p>\r\n	王先生 15112292900\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<br />\r\n</p>'),
(17, '运营助理', '2000-4000', 2, '广州白云', '2017-03-04  09:44', ' <p>\r\n	1、给客户订购原材料；\r\n</p>\r\n<p>\r\n	2、及时回访客户；\r\n</p>\r\n<p>\r\n	3、收集客户在开店过程中遇到的问题；\r\n</p>\r\n<p>\r\n	4、安排客户培训等工作。\r\n</p> ', '<p>\r\n	1、中专以上学历，优秀应届生亦可；\r\n</p>\r\n<p>\r\n	2、沟通能力强；\r\n</p>\r\n<p>\r\n	3、工作态度认真、负责；\r\n</p>\r\n<p>\r\n	4、有经验者优先录取。\r\n</p>\r\n<p>\r\n	<br />\r\n</p>', '<p>\r\n	上班时间：9：00-12：00；13：30-18：00 &nbsp;月休4天\r\n</p>\r\n<p>\r\n	公司包食宿\r\n</p>', '<p>\r\n	王先生 15112292900\r\n</p>\r\n<p>\r\n	<br />\r\n</p>'),
(18, '行政人事经理', '6000-12000', 1, '广州白云', '2017-03-04  09:50', '<p>\r\n	人事方面：\r\n</p>\r\n<p>\r\n	1、组织编制人力资源管理的相关制度，并组织贯彻执行，定期修正；\r\n</p>\r\n<p>\r\n	2、组织编制并落实人力资源发展规划，完成人力资源的挖掘、储备工作；\r\n</p>\r\n<p>\r\n	3、依据公司各部门的需求和岗位任职条件，制定招聘计划，组织面试、复试，完成人员配备；\r\n</p>\r\n<p>\r\n	4、根据公司发展要求，针对各类岗位员工设计培训方案并具体实施；\r\n</p>\r\n<p>\r\n	5、完成公司各部门人员的业绩考核，有效实现绩效考核的目的；\r\n</p>\r\n<p>\r\n	6、负责组织完成员工的薪酬及保险福利等管理；\r\n</p>\r\n<p>\r\n	7、负责员工劳动关系的管理，并对产生的纠纷及其他相关问题进行妥善处理；\r\n</p>\r\n<p>\r\n	8、组织实施对员工的考勤、晋升、调职、奖惩、辞退等全方位管理；\r\n</p>\r\n<p>\r\n	9、与员工沟通、了解思想动态，为员工提供职业规划的专业咨询。\r\n</p>\r\n<p>\r\n	行政方面：\r\n</p>\r\n<p>\r\n	1、组织编制各项行政管理规章制度，并负责监督各项行政管理制度的执行情况；\r\n</p>\r\n<p>\r\n	2、管理公司行政资产及车辆的管理；\r\n</p>\r\n<p>\r\n	3、编制年度行政费用预算并监督实施；\r\n</p>\r\n<p>\r\n	4、妥善安排外来人员的来访接待、大型会议的人员食宿及会务安排；\r\n</p>\r\n<p>\r\n	5、根据公司的经营发展需要，组织管理公司的后勤保障工作。\r\n</p>', '<p>\r\n	1、大专及以上学历，人力资源、心理学专业优先，3年以上人事工作经验，1年以上行政经理管理工作经验；\r\n</p>\r\n<p>\r\n	2、熟悉国家、地方劳动人事政策法规，尤其是社会保险与劳动关系方面的政策法规；\r\n</p>\r\n<p>\r\n	3、熟悉并精通人力资源管理各工作模块的流程及行政管理能力；\r\n</p>\r\n<p>\r\n	4、具有较强的影响力和沟通协调能力，良好的学习能力和文案写作能力，熟练操作Word, Excel, PowerPoint, Outlook 等办公室计算机软件；\r\n</p>\r\n<p>\r\n	5、具有很强的团队领导能力，较强的责任意识和执行力，良好的沟通能力、应变能力及熟练掌握人力资源专业技能；\r\n</p>\r\n<p>\r\n	6、有发现人才的眼光、有快速判断人才特点的能力，并有较强的亲和力。\r\n</p>', '<p>\r\n	工作时间：09：00-12：00 13：30-18：00 &nbsp;月休4天\r\n</p>\r\n<p>\r\n	公司提供食宿\r\n</p>\r\n<p>\r\n	工作地址：广州市白云区平沙智造广场4楼413\r\n</p>', '王先生 15112292900');

